import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/pages/culto-page.css";
import { Alert } from "@material-ui/lab";

import LogoSmall from "../images/logo-small.svg";
import Axios from "axios";

function CultoPage() {
  interface alertDataType {
    type: "success" | "warning" | "info" | "error" | undefined;
    message: string | undefined;
    visible: boolean;
  }

  const [fullName, setFullName] = useState("");
  const [alertData, setAlertData] = useState<alertDataType>({
    type: undefined,
    message: undefined,
    visible: false,
  });
  const [displayAlert, setDisplayAlert] = useState<JSX.Element | undefined>(
    undefined
  );

  async function submitForm() {
    const response = await Axios.post("http://localhost:3003/culto", {
      name: fullName,
    });

    if (response.status === 201) {
      setAlertData({
        type: "success",
        message: "Inscrição realizada com sucesso",
        visible: true,
      });
    } else {
      setAlertData({
        type: "warning",
        message: "Ocorreu um erro ao realizar a inscrição",
        visible: true,
      });
    }

  }

  useEffect(() => {
    setDisplayAlert(
      alertData.visible ? (
        <Alert severity={alertData.type}>{alertData.message}</Alert>
      ) : undefined
    );
  }, [alertData]);

  return (
    <div id="culto-page">
      <Link to="/" className="logo">
        <img src={LogoSmall} alt="Início" />
      </Link>
      <main>
        {displayAlert}
        <h2>Culto de Domingo</h2>
        <input
          type="text"
          value={fullName}
          onChange={(event) => setFullName(event.target.value)}
          placeholder="Nome Completo"
        />

        <Link to="/culto" onClick={submitForm} className="confirm">
          Inscrever-se
        </Link>
        <Link to="/" className="cancel">
          Voltar
        </Link>
      </main>
    </div>
  );
}

export default CultoPage;
