import Axios from "axios";
import React, { useEffect, useState } from "react";

import "../styles/pages/admin-page.css";
import DeleteIcon from "../images/delete.svg";

interface Person {
  id: number;
  name: string;
  scheduled_to: string;
}

function AdminPage() {
  const [users, setUsers] = useState<Person[]>([]);

  async function getPersons() {
    const response = await Axios.get("http://localhost:3003/celula");
    setUsers(response.data);
    console.log(response.data);
  }

  async function deletePerson(id: number) {
    const response = await Axios.delete(`http://localhost:3003/celula/${id}`);
    getPersons();
  }

  const scheduledPersons = users.map((person) => (
    <div className="persons">
      <li key={person.id}>{person.name}</li>
      <img
        className="delete-icon"
        src={DeleteIcon}
        alt="Deletar"
        onClick={() => deletePerson(person.id)}
      />
    </div>
  ));

  useEffect(() => {
    getPersons();
  }, []);

  return (
    <div id="admin-page">
      <div className="content-wrapper">
        <div className="scroll-list">
          <ul>{scheduledPersons}</ul>
        </div>
      </div>
    </div>
  );
}

export default AdminPage;
