import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/pages/culto-page.css";
import { Alert } from "@material-ui/lab";

import LogoSmall from "../images/logo-small.svg";

function CultoPage() {
  interface alertDataType {
    type: "success" | "warning" | "info" | "error" | undefined;
    message: string | undefined;
    visible: boolean;
  }

  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [alertData, setAlertData] = useState<alertDataType>({
    type: undefined,
    message: undefined,
    visible: false,
  });

  function submitForm() {
    if (fullName !== "" && phoneNumber !== "") {
      setAlertData({
        type: "success",
        message: "Inscrição realizada com sucesso",
        visible: true,
      });
      console.log("Form OK");
    } else {
      setAlertData({
        type: "warning",
        message: "Todos os campos devem ser preenchidos",
        visible: true,
      });
    }
    aler
  }

  return (
    <div id="culto-page">
      <Link to="/" className="logo">
        <img src={LogoSmall} alt="Início" />
      </Link>
      <main>
        if(alertData.visible){
            <Alert severity={alertData.type}>{alertData.message}</Alert>
            }
        <h2>Culto de Domingo</h2>
        <input
          type="text"
          value={fullName}
          onChange={(event) => setFullName(event.target.value)}
          placeholder="Nome Completo"
        />
        <input
          type="text"
          value={phoneNumber}
          onChange={(event) => setPhoneNumber(event.target.value)}
          placeholder="Telefone"
        />
        <Link to="/culto" onClick={submitForm} className="confirm">
          Inscrever-se
        </Link>
        <Link to="/" className="cancel">
          Voltar
        </Link>
      </main>
    </div>
  );
}

export default CultoPage;
